import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '100%'
};

// 서울의 초기 위치
const defaultCenter = {
  lat: 37.5665,
  lng: 126.9780
};

const Map = ({ selectedFilters }) => {
  const [currentPosition, setCurrentPosition] = useState(defaultCenter);
  const [markers, setMarkers] = useState([]);

  // 현재 위치 설정
  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setCurrentPosition({
          lat: latitude,
          lng: longitude
        });
      }, () => {
        console.error("Geolocation is not supported or permission denied.");
      });
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  // 선택된 필터에 따라 API 호출 및 마커 설정
  useEffect(() => {
    if (selectedFilters.length === 0) {
      setMarkers([]); // 필터가 선택되지 않으면 마커 초기화
      return;
    }

    const fetchMarkers = async () => {
      const allMarkers = [];
      for (const filter of selectedFilters) {
        console.log(filter)
        const response = await fetch(`http://54.172.167.243:8080/searchOneCategory?address=서울특별시 중구&category=${filter}`);
        const data = await response.json();
        console.log(data)
        const newMarkers = data.map(item => ({
          lat: item.latitude,
          lng: item.longitude,
          name: item.name
        }));
        allMarkers.push(...newMarkers);
      }
      setMarkers(allMarkers);
    };

    fetchMarkers();
  }, [selectedFilters]);

  return (
    <LoadScript googleMapsApiKey="AIzaSyCoBqm6ZJlc2QRPAEZ8Op36nAklnfj5DsE">
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={currentPosition}
        zoom={13}
      >
        {/* 현재 위치 마커 */}
        <Marker position={currentPosition} title="현재 위치" />

        {/* 응답 데이터 마커 */}
        {markers.map((marker, index) => (
          <Marker
            key={index}
            position={{ lat: marker.lat, lng: marker.lng }}
            title={marker.name}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
