import { GoogleMap, LoadScript, Ad, InfoWindow, OverlayView, MarkerF, MarkerClusterer, Marker} from '@react-google-maps/api';
import "./Map.css";
const containerStyle = {
  width: '100%',
  height: '400px'
};

// 서울의 초기 위치
const defaultCenter = {
  lat: 37.5665,
  lng: 126.9780
};
// 각 마커 유형에 따른 아이콘 색상 정의
const markerIcons = {
  "따릉이": "http://maps.google.com/mapfiles/ms/icons/green-dot.png",
  "주차장": "http://maps.google.com/mapfiles/ms/icons/blue-dot.png",
  "문화행사": "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
