    phoneNumber: "02-1234-5680",
    website: "http://gwangjangmarket.com",
    lat: 37.5700,
    lng: 126.9995
  },
  // 관광 거리
  {
    type: "관광거리",
    name: "인사동길",
    contentUrl: "http://example.com/insadongstreet",
    address: "서울특별시 종로구 인사동길 62",
    newAddress: "서울특별시 종로구 인사동 194-2",
    phoneNumber: "번호없음",
