import React, { useState, useEffect } from 'react';
import { setKey, fromLatLng, fromAddress } from 'react-geocode';
import './TextField.css';

const GOOGLE_MAPS_API_KEY = 'AIzaSyCoBqm6ZJlc2QRPAEZ8Op36nAklnfj5DsE'; // Replace with your actual key
setKey(GOOGLE_MAPS_API_KEY);

function LocationSearchField() {
  const inputStyle = {
    color: 'black',
    border: '0px solid gray',
    borderRadius: '5px',
    width: '100%',
    height: '40px',
    margin: '0px 10px',
    outline: 'none',
  };

  const textFieldStyle = {
    flex: 1,
    fontSize: '20px',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
  };

  const [inputValue, setInputValue] = useState('');
  const [currentLocation, setCurrentLocation] = useState('');
  const [searchResults, setSearchResults] = useState([]); // Changed to an array to store multiple results

  // Get current location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          fromLatLng(latitude, longitude)
            .then(({ results }) => {
              if (results && results.length > 0) {
                const address = results[0].formatted_address;
                setCurrentLocation(address);
                setInputValue(address);
              } else {
                setInputValue('Unable to find address.');
              }
            })
            .catch((error) => {
              console.error('Geocoding error:', error);
              setInputValue('Geocoding error.');
            });
        },
        (error) => {
          console.error('Geolocation error:', error);
          setInputValue('Location access denied.');
        }
      );
    } else {
      setInputValue('Geolocation not supported.');
    }
  }, []);

  const handleSearch = () => {
    fromAddress(inputValue)
      .then(({ results }) => {
        if (results && results.length > 0) {
          setSearchResults(results);
        } else {
          setSearchResults([{ formatted_address: '주소를 찾지 못했습니다' }]);
        }
      })
      .catch((error) => {
        console.error('Geocoding error:', error);
        setSearchResults([{ formatted_address: '주소를 찾지 못했습니다.' }]);
      });
  };

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  // Execute search on Enter key press
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  const handleClear = () => {
    setInputValue('');
    setCurrentLocation('');
    setSearchResults([]);
  };

  // When a suggestion is clicked, set it as the search input value
  const handleSuggestionClick = (address) => {
    setInputValue(address);
    setSearchResults([]);
  };

  return (
    <div className='textfield_container'>
      <div className="location_button">
        <button className="button_none">
          <img src="/png/search.png" alt="search" />
        </button>
        <div style={textFieldStyle}>
          <input
            type="text"
            value={inputValue}
            onChange={handleChange}
            onKeyPress={handleKeyPress}
            style={inputStyle}
            placeholder="현재 위치 혹은 원하는 위치를 검색해보세요"
          />
          <button className="button_none" onClick={handleClear}>
            <img src="/png/close.png" alt="close" />
          </button>
        </div>
      </div>

      {/* Display search results */}
      {searchResults.length > 0 && (
        <div className="results_dropdown">
          {searchResults.map((result, index) => (
            <div
              key={index}
              className="result_row"
              onClick={() => handleSuggestionClick(result.formatted_address)}
            >
              {result.formatted_address}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default LocationSearchField;
