import React, { useEffect, useState } from 'react';
import { GoogleMap, InfoWindow, Marker } from '@react-google-maps/api';
import "./Map.css";

const containerStyle = {
  width: '100%',
  height: '100%'
};

const defaultCenter = {
  lat: 37.5665,
  lng: 126.9780
};

const markerIcons = {
  "자연": "http://maps.google.com/mapfiles/ms/icons/green-dot.png",
  "쇼핑": "http://maps.google.com/mapfiles/ms/icons/blue-dot.png",
  "명소": "http://maps.google.com/mapfiles/ms/icons/pink-dot.png",
  "음식": "http://maps.google.com/mapfiles/ms/icons/orange-dot.png",
  "관광거리": "http://maps.google.com/mapfiles/ms/icons/purple-dot.png",
  "외국인": "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
  "문화": "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"
};

const Map = ({ selectedFilters }) => {
  const [currentPosition, setCurrentPosition] = useState(defaultCenter);
  const [district, setDistrict] = useState(''); // 현재 위치의 구 정보를 저장할 상태
  const [markers, setMarkers] = useState([]);
  const [activeMarker, setActiveMarker] = useState(null);

  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const { latitude, longitude } = position.coords;
        setCurrentPosition({ lat: latitude, lng: longitude });

        // 현재 위치의 구 정보 가져오기
        const response = await fetch(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=YOUR_GOOGLE_MAPS_API_KEY`);
        const data = await response.json();
        const addressComponents = data.results[0].address_components;
        const districtComponent = addressComponents.find(component => component.types.includes('administrative_area_level_2'));
        setDistrict(districtComponent.long_name);

      }, () => {
        console.error("Geolocation is not supported or permission denied.");
      });
    }
  }, []);

  useEffect(() => {
    const fetchMarkers = async () => {
      if (district) {
        const allMarkers = [];
        for (const filter of selectedFilters) {
          const response = await fetch(`http://54.172.167.243:8080/searchOneCategory?address=${district}&category=${filter}`);
          const data = await response.json();
          allMarkers.push(...data.map(item => ({
            ...item,
            lat: parseFloat(item.lat),
            lng: parseFloat(item.lng),
            type: filter
          })));
        }
        setMarkers(allMarkers);
      }
    };

    fetchMarkers();
  }, [selectedFilters, district]);

  const handleMarkerClick = (marker) => {
    setActiveMarker(marker);
  };

  const handleInfoWindowClose = () => {
    setActiveMarker(null);
  };

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={currentPosition}
      zoom={13}
    >
      {markers.map((marker, index) => (
        <Marker
          key={index}
          position={{ lat: marker.lat, lng: marker.lng }}
          icon={markerIcons[marker.type] || "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"}
          title={marker.name}
          onClick={() => handleMarkerClick(marker)}
        />
      ))}

      {activeMarker && (
        <InfoWindow
          position={{ lat: activeMarker.lat, lng: activeMarker.lng }}
          onCloseClick={handleInfoWindowClose}
        >
          <div>
            <h4>{activeMarker.name}</h4>
            <p>주소: {activeMarker.address}</p>
            <p>전화번호: {activeMarker.phoneNumber}</p>
            <p>웹사이트: <a href={activeMarker.website} target="_blank" rel="noopener noreferrer">{activeMarker.website}</a></p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
};

export default Map;
