import React, { useState, useEffect } from 'react';
import { setKey, fromLatLng, fromAddress } from "react-geocode";
import "./Home.css";

// Google API 키를 설정합니다.
const GOOGLE_MAPS_API_KEY = "AIzaSyCoBqm6ZJlc2QRPAEZ8Op36nAklnfj5DsE";
setKey(GOOGLE_MAPS_API_KEY);

function LocationSearchField() {
  const inputStyle = {
    color: "black",
    border: "0px solid gray",
    borderRadius: "5px",
    width: "100%",
    height: "40px",
    margin: "0px 10px",
    outline: "none"
  };

  const textFieldStyle = {
    flex: 1,
    fontSize: "20px",
    display: "flex",
    flexDirection: "row",
    alignItems: "center"
  };

  const [inputValue, setInputValue] = useState('');
  const [currentLocation, setCurrentLocation] = useState('');
  const [searchResult, setSearchResult] = useState(''); // 검색 결과 상태 추가

  // 현재 위치 찾기
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;

        // 위도와 경도를 기반으로 역지오코딩
        fromLatLng(latitude, longitude)
          .then(({ results }) => {
            if (results && results.length > 0) {
              const address = results[0].formatted_address;
              setCurrentLocation(address);
              setInputValue(address);
            } else {
              setInputValue("주소를 찾을 수 없습니다.");
            }
          })
          .catch(error => {
            console.error("Geocoding error:", error);
            setInputValue("Geocoding error.");
          });
      }, error => {
        console.error("Geolocation error:", error);
        setInputValue("Location access denied.");
      });
    } else {
      setInputValue("Geolocation not supported.");
    }
  }, []);

  const handleSearch = () => {
    fromAddress(inputValue)
      .then(({ results }) => {
        console.log(results); // API로부터 반환된 결과를 콘솔에 출력하여 확인합니다.
        if (results && results.length > 0) {
          const address = results[0].formatted_address;
          const isSeoul = address.includes("Seoul"); // 반환된 주소에 'Seoul'이 포함되어 있는지 확인합니다.

          if (isSeoul) {
            setCurrentLocation(address); // 검색된 주소를 현재 위치로 설정합니다.
            setSearchResult(`검색 결과: ${address}`); // 검색 결과를 업데이트합니다.
          } else {
            setSearchResult("서울 내에서만 검색할 수 있습니다."); // 서울 외의 지역이 검색된 경우 경고 메시지를 표시합니다.
          }
        } else {
          setSearchResult("주소를 찾을 수 없습니다."); // 검색 결과가 없는 경우 메시지를 표시합니다.
        }
      })
      .catch(error => {
        console.error("Geocoding error:", error);
        setSearchResult("Geocoding error."); // API 호출 중 오류가 발생한 경우 오류 메시지를 표시합니다.
      });
  };

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  // 엔터키로 검색
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  const handleClear = () => {
    setInputValue('');
    setCurrentLocation('');
    setSearchResult('');
  };

  return (
    <div>
      <div className="location_button">
        <button className="button_none">
          <img src="/png/search.png" alt="search" onClick={handleSearch} /> 
        </button>
        <div style={textFieldStyle}>
          <input
            type="text"
            value={inputValue}
            onChange={handleChange}
            onKeyPress={handleKeyPress}
            style={inputStyle}
            placeholder="주소를 입력하거나 현재 위치를 찾으세요"
          />
          <button className="button_none" onClick={handleClear}>
            <img src="/png/close.png" alt="close" />
          </button>
        </div>
      </div>
      <p>현재 위치: {currentLocation}</p>
      <p>{searchResult}</p> {/* 검색 결과를 표시합니다 */}
    </div>
  );
}

export default LocationSearchField;
