import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

import "../Home.css"
import "../weather/weather.css"

import LanSelection from "../language/LanSelection";
import WeatherComponent from "../weather/weather";

function RecommendDetail() {
    const { placeNum, filters } = useParams();  // Corrected to use destructuring directly
    const navigate = useNavigate();

    const [allData, setAllData] = useState({});

    // Translation dictionary to map filters to DB fields
    let filterTrans = {
        "관광거리": "tourStreetKor",
        "명소": "attractions",
        "문화": "entertainment",
        "쇼핑": "shoppings",
        "자연": "nature",
        "음식": "restaurants",
        "외국인": "tourInformations",
    };

    // Fetch data from the API
    const fetchData = async () => {
      try {
        const categories = filters.split(',').map(filter => filterTrans[filter]).join(',');
        const response = await fetch(`http://3.39.223.21:8080/recommend?latitude=37.659223&longitude=127.050265&num=${placeNum}&category=${categories}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setAllData(data);
      } catch (error) {
        console.error("Failed to fetch data:", error);
      }
    };

    useEffect(() => {
        fetchData();
    }, [placeNum, filters]);

    const goBackToHome = () => {
        navigate('/');
    };

    return (
        <div className="app_container">
            <header className="header">
                <div className="app-header-left-content">
                    <h1>관광 코스 추천</h1>
                    <button className="home-button" onClick={goBackToHome}>홈 화면으로 돌아가기</button>
                </div>
                <div className="app-header-right-content">
                    <LanSelection/>
                </div>
            </header>
            <div className="body-content">
                <div className="item-container">
                    {filters.split(',').map((filter, index) => {
                        const key = filterTrans[filter];
                        return (
                            <div className="block_detail" key={index}>
                                <h2>{filter}</h2>
                                {allData[key] && allData[key].length > 0 ? (
                                    allData[key].map((item, itemIndex) => (
                                        <div key={itemIndex}>
                                            <p>Name: {item.name}</p>
                                            <p>Address: {item.address}</p>
                                        </div>
                                    ))
                                ) : (
                                    <p>No data available for {filter}</p>
                                )}
                            </div>
                        );
                    })}
                </div>
            </div>
            <footer className="footer">
                대표 이메일 : dudumchit2023@gmail.com <br/>
                Copyright © 서로감자
            </footer>
        </div>
    );
}

export default RecommendDetail;
