// src/FilterButtons.js
import React, { useState } from 'react';
import "./FilterButtons.css";
import { useLanguage } from './language/LanguageContext';

const FilterButtons = () => {
  const { toggleLanguage } = useLanguage();
  const languages = ['ko', 'en', 'zh']; // 언어 코드
  const languageNames = ["한국어", "English", "中國語"];

  // 초기 선택 인덱스 설정
  const [selectedButtonIndex, setSelectedButtonIndex] = useState(0);

  // 버튼 클릭 이벤트 핸들러
  const handleButtonClick = (index) => {
    setSelectedButtonIndex(index);
    toggleLanguage(languages[index]);
  };

  return (
    <div className="filters">
      {languageNames.map((name, index) => (
        <button
          key={index}
          className={selectedButtonIndex === index ? "selected_filter_button" : "filter_button"}
          onClick={() => handleButtonClick(index)}
        >
          {name}
        </button>
      ))}
    </div>
  );
};

export default FilterButtons;
