// src/language/LanguageContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';

const LanguageContext = createContext();

export const useLanguage = () => useContext(LanguageContext);

export const LanguageProvider = ({ children }) => {
  const [language, setLanguage] = useState('ko');
  const [translatedText, setTranslatedText] = useState({
    title: "서울을 찾아서",
    subtitle: "숨겨진 서울의 모습 찾아서, AI 기반 관광 추천 서비스",
    weatherToday: "오늘의 관광날씨는?",
    hotPlacesTop5: "서울의 핫플 Top5",
    findNearby: "주변 관광지 찾아보기",
    recommendTour: "관광 코스 추천 받기",
    numPlaces: "장소의 갯수를 선택해주세요",
    selectCategory: "원하는 분류를 선택해주세요 (복수선택 가능)",
    buttonGetRecommendation: "추천 받기",
    footer: "대표 이메일 : dudumchit2023@gmail.com\nCopyright © 서로감자",
  });

  const translateText = async (text, targetLang) => {
    const apiKey = 'YOUR_API_KEY_HERE';  // API 키를 여기에 넣으세요
    const url = `https://translation.googleapis.com/language/translate/v2?key=${apiKey}`;
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          q: text,
          target: targetLang
        })
      });
      const data = await response.json();
      return data.data.translations[0].translatedText;
    } catch (error) {
      console.error('Error translating text:', error);
      return text; // 에러 시 원본 텍스트 반환
    }
  };

  const translateAllTexts = async (lang) => {
    const texts = [
      "서울을 찾아서", "숨겨진 서울의 모습 찾아서, AI 기반 관광 추천 서비스",
      "오늘의 관광날씨는?", "서울의 핫플 Top5", "주변 관광지 찾아보기",
      "관광 코스 추천 받기", "장소의 갯수를 선택해주세요", "원하는 분류를 선택해주세요 (복수선택 가능)",
      "추천 받기", "대표 이메일 : dudumchit2023@gmail.com\nCopyright © 서로감자"
    ];

    const keys = [
      "title", "subtitle", "weatherToday", "hotPlacesTop5", "findNearby",
      "recommendTour", "numPlaces", "selectCategory", "buttonGetRecommendation", "footer"
    ];

    const translatedTexts = await Promise.all(
      texts.map(text => translateText(text, lang))
    );

    const newTranslatedText = keys.reduce((acc, key, index) => {
      acc[key] = translatedTexts[index];
      return acc;
    }, {});

    setTranslatedText(newTranslatedText);
  };

  useEffect(() => {
    translateAllTexts(language);
  }, [language]);

  const toggleLanguage = (lang) => {
    setLanguage(lang);
  };

  return (
    <LanguageContext.Provider value={{ language, translatedText, toggleLanguage }}>
      {children}
    </LanguageContext.Provider>
  );
};
