import React, { useState, useEffect } from 'react';
import './HotPlace.css';

const SEOUL_OPEN_DATA_AUTH_KEY = "515653596b79756a38384a77506645" // 서울 열린 데이터
const AREA_CODES = [
  "POI001", "POI002", "POI003", "POI004", "POI005", "POI006", "POI007", "POI008",
  // ... 중략: 나머지 코드 모두 추가
  "POI115"
];
// // 1부터 115까지 "POI001", "POI002", ..., "POI115" 형식의 리스트 생성
// const AREA_CODES = Array.from({ length: 115 }, (_, index) => {
//     const number = (index + 1).toString().padStart(3, '0'); // 3자리 숫자로 포맷
//     return `POI${number}`;
//   });
function HotPlaces() {
  const [hotPlaces, setHotPlaces] = useState([]);

  const fetchHotPlaces = async () => {
    const results = [];

    // 각 장소에 대해 비동기적으로 혼잡도 데이터를 가져옵니다.
    const fetchPromises = AREA_CODES.map(async (code) => {
      try {
        const response = await fetch(`http://openapi.seoul.go.kr:8088/${SEOUL_OPEN_DATA_AUTH_KEY}/json/citydata_ppltn/1/5/${code}`);
        const data = await response.json();
        console.log(data.SeoulRtd.citydata_ppltn)
        if (data.citydata_ppltn && data.citydata_ppltn.row.length > 0) {
          results.push(data.citydata_ppltn.row[0]);
        }
      } catch (error) {
        console.error(`Error fetching data for ${code}:`, error);
      }
    });

    // 모든 요청이 완료될 때까지 기다립니다.
    await Promise.all(fetchPromises);

    // 혼잡도 기준으로 정렬하여 상위 5개의 핫플레이스만 추출합니다.
    const topHotPlaces = results
      .sort((a, b) => b.LIVE_PPLTN_STTS - a.LIVE_PPLTN_STTS)
      .slice(0, 5);

    setHotPlaces(topHotPlaces);
  };

  useEffect(() => {
    fetchHotPlaces();
  }, []);

  return (
    <div className="hot-places-container">
      <h2>가장 혼잡한 서울의 핫플레이스 5곳</h2>
      <ul className="hot-places-list">
        {hotPlaces.map((place) => (
          <li key={place.AREA_CD} className="hot-place-item">
            <h3>{place.AREA_NM}</h3>
            <p>실시간 인구 현황: {place.LIVE_PPLTN_STTS}</p>
            <p>장소 혼잡도: {place.AREA_CONGEST_MSG}</p>
            <p>최소 인구: {place.AREA_PPLTN_MIN}, 최대 인구: {place.AREA_PPLTN_MAX}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HotPlaces;
