// HotPlaces.js
import React, { useState, useEffect } from 'react';
import './HotPlace.css';

const SEOUL_OPEN_DATA_AUTH_KEY = "515653596b79756a38384a77506645"; // Replace with actual API key

function HotPlaces() {

  const fetchHotPlaces = async () => {
    try {
      // 서울 열린 데이터 API를 통해 모든 관광특구의 실시간 인구 현황을 가져옵니다.
      const response = await fetch(`http://openapi.seoul.go.kr:8088/${SEOUL_OPEN_DATA_AUTH_KEY}/json/TouristSpotCongestion/1/115`);
      const data = await response.json();
      const spots = data.TOURISTSPOTCONGESTION.row;
      console.log("log---[핫플 받기 확인]", data);

      // 실시간 혼잡도를 기준으로 정렬하고 상위 5개의 관광특구만 추출합니다.
      const topHotPlaces = spots
        .sort((a, b) => b.LIVE_PPLTN_STTS - a.LIVE_PPLTN_STTS)
        .slice(0, 5);

    } catch (error) {
      console.error('Error fetching hot places:', error);
    }
  };

  useEffect(() => {
    fetchHotPlaces();
  }, []);

  return (
    <div className="hot-places-container">
      <h2>가장 혼잡한 서울의 핫플레이스 5곳</h2>
      <ul className="hot-places-list">
        {hotPlaces.map((place) => (
          <li key={place.AREA_CD} className="hot-place-item">
            <h3>{place.AREA_NM}</h3>
            <p>실시간 인구 현황: {place.LIVE_PPLTN_STTS}</p>
            <p>장소 혼잡도: {place.AREA_CONGEST_MSG}</p>
            <p>최소 인구: {place.AREA_PPLTN_MIN}, 최대 인구: {place.AREA_PPLTN_MAX}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HotPlaces;
