import React, { useState, useEffect } from 'react';
import "./Home.css";
import axios from 'axios';

function TextField() {
  const inputStyle = {
    color: "black",
    border: "0px solid gray",
    borderRadius: "5px",
    width: "100%",
    height: "40px",
    margin: "0px 10px",
    outline: "none"
  };

  const textFieldStyle = {
    flex: 1,
    fontSize: "20px",
    display: "flex",
    flexDirection: "row",
    alignItems: "center"
  };

  const [inputValue, setInputValue] = useState('');

useEffect(() => {
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(async (position) => {
            const { latitude, longitude } = position.coords;
            const coords = `${longitude},${latitude}`;

            try {
                const response = await axios.get('http://localhost:3001/api/proxy', {
                    params: {
                        coords,
                        sourcecrs: 'epsg:4326',
                        orders: 'legalcode',
                        output: 'json'
                    }
                });

                const data = response.data;
                if (data.results && data.results.length > 0) {
                    // 주소 결과를 입력 값으로 설정
                    setInputValue(
                        data.results[0].region.area1.name + " " +
                        data.results[0].region.area2.name + " " +
                        data.results[0].region.area3.name
                    );
                } else {
                    setInputValue("주소를 찾을 수 없습니다.");
                }
            } catch (error) {
                console.log('Error during geocoding:', error);
                setInputValue("Geocoding error.");
            }
        }, (error) => {
            console.log('Geolocation error:', error);
            setInputValue("Location access denied.");
        });
    } else {
        setInputValue("Geolocation not supported.");
    }
}, []);

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleClear = () => {
    setInputValue('');
  };

  return (
    <div style={textFieldStyle}>
      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        style={inputStyle}
        placeholder="원하는 주소를 입력해주세요"
      />
      <button className="button_none" onClick={handleClear}>
        <img src="/png/close.png" alt="close" />
      </button>
    </div>
  );
}

export default TextField;
