import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import "./Map.css";

const containerStyle = {
  width: '100%',
  height: '400px'
};

const defaultCenter = {
  lat: 37.5665,
  lng: 126.9780
};

const markerIcons = {
  "자전거": "http://maps.google.com/mapfiles/ms/icons/green-dot.png",
  "주차장": "http://maps.google.com/mapfiles/ms/icons/blue-dot.png",
  "문화행사": "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
  "지하철": "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"
};

const Map = ({ inf1, inf2, inf3, inf4 }) => {
  const [currentPosition, setCurrentPosition] = useState(defaultCenter);
  const [markers, setMarkers] = useState([]);

  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setCurrentPosition({
          lat: latitude,
          lng: longitude
        });
      }, () => {
        console.error("Geolocation is not supported or permission denied.");
      });
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  useEffect(() => {
    // inf1, inf2, inf3, inf4 정보가 모두 유효한 경우에만 처리
    if (inf1 && inf2 && inf3 && inf4) {
      const allMarkers = integrateData(inf1,inf2,inf3,inf4);
      setMarkers(allMarkers);
    }
  }, [inf1, inf2, inf3, inf4]);
  const integrateData = (inf1, inf2, inf3, inf4) => {
    const markers = [];

    // 문화 정보 처리
    inf1.forEach((event) => {
        const { EVENT_X, EVENT_Y, EVENT_NM, EVENT_PLACE, EVENT_PERIOD, URL, THUMBNAIL } = event;
        markers.push({
            lat: parseFloat(EVENT_Y),
            lng: parseFloat(EVENT_X),
            type: "문화",
            detail: {
                name: EVENT_NM,
                place: EVENT_PLACE,
                period: EVENT_PERIOD,
                url: URL,
                thumbnail: THUMBNAIL
            }
        });
    });

    // 주차장 정보 처리
    inf2.forEach((parking) => {
        const { LAT, LNG, PRK_NM, ADDRESS, RATES, PAY_YN } = parking;
        markers.push({
            lat: parseFloat(LAT),
            lng: parseFloat(LNG),
            type: "주차장",
            detail: {
                name: PRK_NM,
                address: ADDRESS,
                rates: RATES,
                pay: PAY_YN
            }
        });
    });

    // 지하철 정보 처리
    inf3.forEach((subway) => {
        const { SUB_STN_X, SUB_STN_Y, SUB_STN_NM, SUB_STN_LINE, SUB_STN_RADDR, SUB_STN_JIBUN } = subway;
        markers.push({
            lat: parseFloat(SUB_STN_Y),
            lng: parseFloat(SUB_STN_X),
            type: "지하철",
            detail: {
                name: SUB_STN_NM,
                line: SUB_STN_LINE,
                address: SUB_STN_RADDR,
                jibun: SUB_STN_JIBUN
            }
        });
    });

    // 자전거 정보 처리
    inf4.forEach((bike) => {
        const { SBIKE_X, SBIKE_Y, SBIKE_SPOT_NM, SBIKE_SHARED, SBIKE_PARKING_CNT } = bike;
        markers.push({
            lat: parseFloat(SBIKE_Y),
            lng: parseFloat(SBIKE_X),
            type: "자전거",
            detail: {
                name: SBIKE_SPOT_NM,
                shared: SBIKE_SHARED,
                parkingCnt: SBIKE_PARKING_CNT
            }
        });
    });

    return markers;
  }
  return (
    <LoadScript googleMapsApiKey="AIzaSyCoBqm6ZJlc2QRPAEZ8Op36nAklnfj5DsE">
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={currentPosition}
        zoom={13}
      >
        {/* 현재 위치 마커 */}
        <Marker position={currentPosition} title="현재 위치" />

        {/* 응답 데이터 마커 */}
        {markers.map((marker, index) => (
          <Marker
            key={index}
            position={{ lat: marker.lat, lng: marker.lng }}
            icon={markerIcons[marker.type] || "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"}
            title={marker.name}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
