import React, { useState, useEffect } from 'react';
import "./App.css";

function TextField() {
  const inputStyle = {
    color: "black",
    border: "0px solid gray",
    borderRadius: "5px",
    width: "100%",
    height: "40px",
    margin: "0px 10px",
    outline: "none"
  };

  const textFieldStyle = {
    flex: 1,
    fontSize: "20px",
    display: "flex",
    flexDirection: "row",
    alignItems: "center"
  };

  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        // API 호출을 위한 URL 구성
        const url = `https://naveropenapi.apigw.ntruss.com/map-reversegeocode/v2/gc?coords=${longitude},${latitude}&output=json`;

        // API 요청
        fetch(url, {
          method: 'GET',
          headers: {
            "X-NCP-APIGW-API-KEY-ID": "lGxjBhxP0WYiGGuLFq9O",
            "X-NCP-APIGW-API-KEY": "ctfHKlPln4"
          }
        })
        .then(response => response.json())
        .then(data => {
          if (data.results && data.results.length > 0) {
            // 가장 관련이 높은 결과의 주소를 가져옵니다.
            setInputValue(data.results[0].region.area1.name + " " + 
                          data.results[0].region.area2.name + " " + 
                          data.results[0].region.area3.name);
          } else {
            setInputValue("주소를 찾을 수 없습니다.");
          }
        })
        .catch(error => {
          console.error('Error during geocoding:', error);
          setInputValue("Geocoding error.");
        });
      }, (error) => {
        console.error('Geolocation error:', error);
        setInputValue("Location access denied.");
      });
    } else {
      setInputValue("Geolocation not supported.");
    }
  }, []);

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleClear = () => {
    setInputValue('');
  };

  return (
    <div style={textFieldStyle}>
      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        style={inputStyle}
        placeholder="원하는 위치를 입력하세요"
      />
      <button className="button_none" onClick={handleClear}>
        <img src="/png/close.png" alt="close" />
      </button>
    </div>
  );
}

export default TextField;
