import React, { useEffect, useState } from 'react';
import { GoogleMap, InfoWindow, Marker } from '@react-google-maps/api';
import "./Map.css";

const containerStyle = {
  width: '100%',
  height: '100%'
};

const defaultCenter = {
  lat: 37.5665,
  lng: 126.9780
};

const markerIcons = {
  "자연": "http://maps.google.com/mapfiles/ms/icons/green-dot.png",
  "쇼핑": "http://maps.google.com/mapfiles/ms/icons/blue-dot.png",
  "명소": "http://maps.google.com/mapfiles/ms/icons/pink-dot.png",
  "음식": "http://maps.google.com/mapfiles/ms/icons/orange-dot.png",
  "관광거리": "http://maps.google.com/mapfiles/ms/icons/purple-dot.png",
  "외국인": "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
  "문화": "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png"
};

const Map = ({ selectedFilters }) => {
  const [currentPosition, setCurrentPosition] = useState(defaultCenter);
  const [markers, setMarkers] = useState([]);
  const [activeMarker, setActiveMarker] = useState(null);
  const [currentDistrict, setCurrentDistrict] = useState("");

  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(async (position) => {
        const { latitude, longitude } = position.coords;
        setCurrentPosition({ lat: latitude, lng: longitude });

        const district = await getDistrictName(latitude, longitude);
        setCurrentDistrict(district);
      }, () => {
        console.error("Geolocation is not supported or permission denied.");
      });
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  useEffect(() => {
    if (currentDistrict) {
      fetchData(currentDistrict, selectedFilters);
    }
  }, [currentDistrict, selectedFilters]);

  const getDistrictName = async (lat, lng) => {
    const geocodeResponse = await fetch(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=YOUR_GOOGLE_MAPS_API_KEY`);
    const geocodeData = await geocodeResponse.json();
    const addressComponents = geocodeData.results[0].address_components;
    console.log("에러확인중..")
    const districtComponent = addressComponents.find(component => component.types.includes('political') && component.types.includes('sublocality_level_1'));

    return districtComponent ? districtComponent.long_name : '';
  };

  const fetchData = async (district, filters) => {
    try {
      const category = filters.join(','); // Assuming multiple filters can be selected
      // const response = await fetch(`http://54.172.167.243:8080/searchOneCategory?address=${district}&category=${category}`);
      const response = await fetch(`http://54.172.167.243:8080/searchOneCategory?address=중구&category=${category}`);
      const data = await response.json();
      console.log(data)
      setMarkers(data); // Assuming data is an array of marker objects
    } catch (error) {
      console.error("Failed to fetch data:", error);
    }
  };

  const handleMarkerClick = (marker) => {
    setActiveMarker(marker);
  };

  const handleInfoWindowClose = () => {
    setActiveMarker(null);
  };

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={currentPosition}
      zoom={13}
    >
      <Marker position={currentPosition} title="현재 위치" />

      {markers.map((marker, index) => (
        <Marker
          key={index}
          position={{ lat: marker.lat, lng: marker.lng }}
          icon={markerIcons[marker.type]}
          title={marker.name}
          onClick={() => handleMarkerClick(marker)}
        />
      ))}

      {activeMarker && (
        <InfoWindow
          position={{ lat: activeMarker.lat, lng: activeMarker.lng }}
          onCloseClick={handleInfoWindowClose}
        >
          <div>
            <h4>{activeMarker.name}</h4>
            <p>주소: {activeMarker.address}</p>
            <p>전화번호: {activeMarker.phoneNumber}</p>
            <p>웹사이트: {activeMarker.website}</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
};

export default Map;
