// RecommendDetail.js

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

import "../Home.css"
import "../weather/weather.css"

import LanSelection from "../language/LanSelection";
import WeatherComponent from "../weather/weather";


function HotPlaceDetail() {

    const { placeNum } = useParams();
    const { filters } = useParams();
    const navigate = useNavigate();

    const [allData, setAllData] = useState({}); // API에서 불러온 모든 마커 데이터 저장
    const [filteredMarkers, setFilteredMarkers] = useState([]); // 필터링된 마커 데이터를 저장
    const [activeMarker, setActiveMarker] = useState(null);

    // 현재 위치를 설정하는 useEffect
    useEffect(() => {
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition((position) => {
          const { latitude, longitude } = position.coords;
          setCurrentPosition({
            lat: latitude,
            lng: longitude
          });
          console.log("Current Position:", latitude, longitude); // 현재 위치 로그 출력
        }, () => {
          console.error("Geolocation is not supported or permission denied.");
        });
      } else {
        console.error("Geolocation is not supported by this browser.");
      }
    }, []);

    // 각 카테고리별로 데이터를 불러오는 함수
    const fetchData = async () => {
      try {
        const response = await fetch(`http://3.39.223.21:8080/recommend?latitude=37.659223&longitude=127.050265&num=${placeNum}&category=${filters}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log("데이터 확인 : ", data);
      } catch (error) {
        console.error(`Failed to fetch data for category ${category}:`, error);
        return { [category]: [] }; // 에러가 발생했을 경우 빈 배열 반환
      }
    };

    // 처음 마운트될 때 추천 데이터를 불러옴
    useEffect(() => {
        console.log("log------------------")
        fetchData();
        setAllMarkers(allData); // allMarkers 상태를 업데이트
    }, []);


    // 홈 화면으로 돌아가는 함수
    const goBackToHome = () => {
        navigate('/');

    };
    return (
        <div className="app_container">
        <header className="header">
          <div className="app-header-left-content">
            <h1>관광 코스 추천</h1>
            {/* <h4>숨겨진 서울의 모습 찾아서, AI 기반 관광 추천 서비스</h4> */}
             {/* 받아온 데이터를 표시 */}
             <button class="home-button" onClick={goBackToHome}> 홈 화면으로 돌아가기</button>
          </div>
          <div className="app-header-right-content">
           <LanSelection/>
          </div>
        </header>

        <div className="body-content">

          <div className="item-container">
              <div className="block_detail">
                <h2>{placeNum} 의 날씨</h2>




            </div>

            <div className="block_detail2">
              <h2>{placeNum} 의 도로 상황</h2>
              <div className="mes_title"> 도로 혼잡도 <br/> </div>
                <div className="mes"> ------ <br/></div>

            </div>
          </div>



        </div>

        <footer className="footer">
          대표 이메일 : dudumchit2023@gmail.com <br></br>
          Copyright © 서로감자
        </footer>

        </div>


      );

}

export default HotPlaceDetail;
