import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function RecommendDetail() {
    const { placeNum, filters } = useParams();
    const navigate = useNavigate();
    const [data, setData] = useState({});

    // Fetch data
    const fetchData = async () => {
      try {
        const url = `http://3.39.223.21:8080/recommend?latitude=37.659223&longitude=127.050265&num=${placeNum}&category=${filters}`;
        const response = await fetch(url);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const fetchedData = await response.json();
        setData(fetchedData);
      } catch (error) {
        console.error("Failed to fetch data:", error);
      }
    };

    useEffect(() => {
        fetchData();
    }, [placeNum, filters]);

    const goBackToHome = () => navigate('/');

    // Render function for each category
    const renderCategory = (category) => {
        if (!data[category] || data[category].length === 0) {
            return <p>No data available for {category}</p>;
        }

        return (
            <ul className="category-list">
                {data[category].map((item, index) => (
                    <li key={index}>
                        <h3>{item.name}</h3>
                        <p>Address: {item.new_address || item.old_address}</p>
                        <p>Phone: {item.phone_number}</p>
                        {item.website && <p>Website: <a href={item.website} target="_blank" rel="noopener noreferrer">{item.website}</a></p>}
                    </li>
                ))}
            </ul>
        );
    };

    return (
        <div className="app_container">
            <header className="header">
                <h1>관광 코스 추천</h1>
                <button className="home-button" onClick={goBackToHome}>홈 화면으로 돌아가기</button>
            </header>
            <div className="body-content">
                {filters.split(',').map((filter, index) => (
                    <div className="block_detail" key={index}>
                        <h2>{filter}</h2>
                        {renderCategory(filter)}
                    </div>
                ))}
            </div>
            <footer className="footer">
                대표 이메일: dudumchit2023@gmail.com
            </footer>
        </div>
    );
}

export default RecommendDetail;
