// src/Home.js
import React, { useState } from 'react';
import Map from './Map';
import { useLanguage, LanguageProvider } from './language/LanguageContext';
import "./Home.css";
import LocationSearchField from "./TextField";
import FilterButtons from "./FilterButtons";
import ReFilterButtons from "./ReFilterButtons";
import NumFilterButtons from "./NumFilterButtons";
import LanSelection from "./LanSelection";
import WeatherComponent from "./weather/weather";
import HotPlaces from './hotPlace/HotPlace';

const HomeContent = () => {
  const { translatedText } = useLanguage();
  const [weatherData, setWeatherData] = useState(null);

  return (
    <div className="app_container">
      <header className="header">
        <div className="app-header-left-content">
          <h1>{translatedText.title}</h1>
          <h4>{translatedText.subtitle}</h4>
        </div>
        <div className="app-header-right-content">
          <LanSelection />
        </div>
      </header>

      <div className="body-content">
        <div className="block">
          <LocationSearchField setWeatherData={setWeatherData} />
        </div>
        <div className="item-container">
          <div className="double-item">
            <div className="block_weather">
              <h2>{translatedText.weatherToday}</h2>
              {weatherData && (
                <WeatherComponent
                  temperature={weatherData.temperature}
                  precipitation={weatherData.precipitation}
                  humidity={weatherData.humidity}
                  windSpeed={weatherData.windSpeed}
                  mes_air={weatherData.mes_air}
                  mes_uv={weatherData.mes_uv}
                  mes_rain={weatherData.mes_rain}
                />
              )}
            </div>
            <div className="block_top">
              <h2>{translatedText.hotPlacesTop5}</h2>
              <HotPlaces />
            </div>
          </div>

          <div className="block2">
            <h2>{translatedText.findNearby}</h2>
            필터 (복수 선택 가능)
            <ReFilterButtons />
            <Map />
          </div>
        </div>

        <div className="block">
          <h2>{translatedText.recommendTour}</h2>
          {translatedText.numPlaces}
          <NumFilterButtons />
          {translatedText.selectCategory}
          <FilterButtons />
          <div style={{ textAlign: "center" }}>
            <button className="recommand_button">{translatedText.buttonGetRecommendation}</button>
          </div>
        </div>
      </div>

      <footer className="footer">{translatedText.footer}</footer>
    </div>
  );
};

function App() {
  return (
      <HomeContent />
  );
}

export default App;
